                                }
                                Main.music[l].SetVariable("Volume", Main.musicFade[l] * Main.musicVolume * num8);
                            }
                        }
                        else if (Main.music[l].IsPlaying)
                        {
                            if (Main.musicFade[Main.curMusic] > 0.25f)
                            {
                                Main.musicFade[l] -= 0.005f;
                            }
                            else if (Main.curMusic == 0)
                            {
                                Main.musicFade[l] = 0f;
                            }
                            if (Main.musicFade[l] <= 0f)
                            {
                                Main.musicFade[l] -= 0f;
                                Main.music[l].Stop(AudioStopOptions.Immediate);
                            }
                            else
                            {
                                Main.music[l].SetVariable("Volume", Main.musicFade[l] * Main.musicVolume * num8);
                            }
                        }
                        else
                        {
                            Main.musicFade[l] = 0f;
                        }
                    }
                    if (Main.musicError > 0)
                    {
                        Main.musicError--;
                    }
                }
            }
            catch
            {
                Main.musicError++;
                if (Main.musicError >= 100)
                {
                    Main.musicError = 0;
