        // SelectPlayer
        public static void SelectPlayer(PlayerFileData data)
        {
            if (Main._pendingCharacterSelect != null)
            {
                Main._pendingCharacterSelect(data);
                Main._pendingCharacterSelect = null;
                return;
            }
            if (!Main.menuMultiplayer)
            {
                Main.ServerSideCharacter = false;
                Main.myPlayer = 0;
                data.SetAsActive();
                Main.player[Main.myPlayer].position = Vector2.Zero;
                Main.LoadWorlds();
                Main.PlaySound(10, -1, -1, 1);
                Main.menuMode = 6;
                return;
            }
            Main.ServerSideCharacter = false;
            data.SetAsActive();
            Main.PlaySound(10, -1, -1, 1);
            if (Main.autoJoin)
            {
                if (Netplay.SetRemoteIP(Main.getIP))
                {
                    Main.menuMode = 10;
                    Netplay.StartTcpClient();
                }
                Main.autoJoin = false;
                return;
            }
            if (Main.menuServer)
            {
                Main.LoadWorlds();
                Main.menuMode = 6;
                return;
            }
            Main.menuMode = 13;
            Main.clrInput();
        }


